pragma solidity ^0.4.16;

contract ItemFactory  {
    // uint dnaDigits = 16;
    // uint dnaModulus = 10 ** dnaDigits;
    // uint lookUpFee = 0.001 ether;

    struct User {
        //string name;
        address name;
        uint32 level;
        uint flag;
        uint id;
    }

    User[] public users;
    mapping (uint => address) userid2address;
    mapping (uint => uint[]) userid2Itemids;
    mapping (address => User) username2user;

    struct OneComment {
        string text;
        uint rating;
        uint8 flag;
        address useraddress;
    }


    struct Comments{
        OneComment[]  comments;
        uint flag;
    }

    mapping (uint => Comments) item2Comments;

    function _createUser(address _username) internal {
        uint id = users.push(User(_username, 1,1, 0)) - 1;
        users[id].id = id;
        userid2address[id] = msg.sender;
        username2user[_username] = User(_username, 1, 1, id);

    }


    function _writeComment(uint _itemid, string _commentTxt) public {

     if(username2user[msg.sender].flag != 1) {

            _createUser(msg.sender);
    }

        // uint uid = username2user[msg.sender].id;
        // require(msg.sender == userid2address[uid]);

         if(item2Comments[_itemid].flag == 1) {
            item2Comments[_itemid].comments.push(OneComment(_commentTxt, 1, 1, msg.sender));
            item2Comments[_itemid].flag = 1;
         }
         else {
            OneComment[] temp;
            temp.push(OneComment(_commentTxt, 1, 1, msg.sender));
            item2Comments[_itemid].comments = temp;
            item2Comments[_itemid].flag = 1;
        }

    }


    function _readComment(uint32 _itemid) external view returns (uint) {
        string memory allResult;
        if(item2Comments[_itemid].flag == 1) {
            return item2Comments[_itemid].comments.length;
        }
        else
            return 100000;
    }

}
